examples:
tags = system.opc.browse()
for row in tags:
    print row.getOpcServer(), row.getOpcItemPath(), row.getType()

tags = system.opc.browseSimple('Ignition OPC UA Server', 'SLC', '*B3*', None)
for tag in tags:
    print tag.getOpcItemPath()

servers = system.opc.getServers()
for server in servers:
    print server

allServers = system.opc.getServers(includeDisabled=True)
for server in allServers:
    print '%s : %s' % (server, system.opc.isServerEnabled(server))

# Enable all disabled servers
for server in system.opc.getServers(includeDisabled=True):
    if not system.opc.isServerEnabled(server):
        system.opc.setServerEnabled(server, True)
